import axios from 'axios';
import MenusComponent from '@/components/shop/MenusComponent.vue';
import ProductsComponent from '@/components/shop/ProductsComponent.vue';
export default {
  name: 'ProductsView',
  components: {
    MenusComponent: MenusComponent,
    ProductsComponent: ProductsComponent
  },
  data: function data() {
    return {
      restaurant: {},
      restaurants: [],
      menus: [],
      products: []
    };
  },
  methods: {
    checkProductsByRestaurant: function checkProductsByRestaurant(category) {
      for (var i = 0; i < this.products.length; i++) {
        if (this.products[i].categories.includes(category) && this.products[i].restaurantId === this.restaurant._id) {
          return true;
        }
      }

      return false;
    }
  },
  computed: {
    checkMenusByRestaurant: function checkMenusByRestaurant() {
      for (var i = 0; i < this.menus.length; i++) {
        if (this.menus[i].restaurantId === this.restaurant._id) {
          return true;
        }
      }

      return false;
    },
    getAllCategories: function getAllCategories() {
      var categories = [];

      for (var i = 0; i < this.products.length; i++) {
        var productCategories = this.products[i].categories;

        for (var j = 0; j < productCategories.length; j++) {
          if (!categories.includes(productCategories[j])) {
            categories.push(productCategories[j]);
          }
        }
      }

      return categories;
    }
  },
  mounted: function mounted() {
    var _this = this;

    axios.get('http://localhost:4200/api/v1/restaurants/').then(function (response) {
      return _this.restaurants = response.data;
    }).catch(function (error) {
      return console.log(error);
    });
    axios.get('http://localhost:4200/api/v1/restaurants/' + this.$route.params.id).then(function (response) {
      return _this.restaurant = response.data;
    }).catch(function (error) {
      return console.log(error);
    });
    axios.get('http://localhost:4600/api/v1/menus/').then(function (response) {
      return _this.menus = response.data;
    }).catch(function (error) {
      return console.log(error);
    });
    axios.get('http://localhost:4500/api/v1/products/').then(function (response) {
      return _this.products = response.data;
    }).catch(function (error) {
      return console.log(error);
    });
  }
};
