import axios from 'axios';
export default {
  name: 'ValidateDeliveryComponent',
  props: {
    delivery: {},
    action: String
  },
  data: function data() {
    return {
      newDeliveryMan: {},
      validDelivery: false,
      validateDeliveryDialog: false,
      show1: true
    };
  },
  methods: {
    validateDelivery: function validateDelivery() {
      if (this.action === 'Accepter') {
        axios.patch('http://localhost:4300/api/v1/deliveries/' + this.delivery._id, {
          deliveryState: 'En cours de livraison'
        }).catch(function (error) {
          return console.log(error);
        });
        axios.patch('http://localhost:4400/api/v1/orders/' + this.delivery.order._id, {
          orderState: 'En cours de livraison'
        }).catch(function (error) {
          return console.log(error);
        });
      }

      if (this.action === 'Refuser') {
        axios.patch('http://localhost:4300/api/v1/deliveries/' + this.delivery._id, {
          deliveryManId: this.newDeliveryMan.id
        }).catch(function (error) {
          return console.log(error);
        });
      }

      if (this.action === 'Confirmer') {
        axios.patch('http://localhost:4300/api/v1/deliveries/' + this.delivery._id, {
          deliveryState: 'Livrée'
        }).catch(function (error) {
          return console.log(error);
        });
        axios.patch('http://localhost:4400/api/v1/orders/' + this.delivery.order._id, {
          orderState: 'Livrée'
        }).catch(function (error) {
          return console.log(error);
        });
      }

      location.reload();
    },
    cancelButtonClick: function cancelButtonClick() {
      this.validateDeliveryDialog = false;
      this.$refs.form.reset();
    },
    validateDeliverySubmit: function validateDeliverySubmit() {
      if (this.validDelivery) {
        this.$refs.form.reset();
        this.validateDeliveryDialog = false;
      }
    }
  },
  mounted: function mounted() {
    var _this = this;

    axios.get('http://localhost:4300/api/v1/deliveries/deliveryman/' + this.delivery.deliveryManId).then(function (response) {
      return _this.newDeliveryMan = response.data;
    }).catch(function (error) {
      return console.log(error);
    });
  }
};
